import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LandingPage.css';

function SignupPage() {
  const [form, setForm] = useState({
    username: '',
    password: '',
    name: '',
    address: '',
    age: '',
    email: ''
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSignup = async () => {
    const { username, password, name, address, age, email } = form;
    if (!username || !password || !name || !address || !age || !email) {
      setMessage('すべての項目を入力してください');
      return;
    }
    try {
      const res = await axios.post('http://localhost:5000/api/signup', form);
      if (res.data.success) {
        alert('登録が完了しました！ログインしてください');
        navigate('/login');
      }
    } catch (err) {
      setMessage(err.response?.data?.message || '登録に失敗しました');
    }
  };

  return (
    <div className="landing-container" style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundImage: 'url(/images/landing.jpg)', backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <div style={{ backgroundColor: 'rgba(255,255,255,0.95)', padding: '40px', borderRadius: '12px', boxShadow: '0 8px 16px rgba(0,0,0,0.2)', width: '360px', textAlign: 'center' }}>
        <h2>新規登録</h2>
        {['username', 'password', 'name', 'address', 'age', 'email'].map((field, index) => (
          <input
            key={index}
            type={field === 'password' ? 'password' : field === 'age' ? 'number' : 'text'}
            name={field}
            placeholder={field === 'username' ? 'ユーザID' : field === 'password' ? 'パスワード' : field === 'name' ? '名前' : field === 'address' ? '住所' : field === 'age' ? '年齢' : 'メールアドレス'}
            value={form[field]}
            onChange={handleChange}
            style={{ width: '100%', padding: '10px', margin: '8px 0' }}
          />
        ))}
        <button onClick={handleSignup} style={{ width: '100%', padding: '10px', backgroundColor: '#4f46e5', color: 'white', border: 'none', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px' }}>
          登録
        </button>
        {message && <p style={{ color: 'red', marginTop: '10px' }}>{message}</p>}
      </div>
    </div>
  );
}

export default SignupPage;
