import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

function HomePage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');

  useEffect(() => {
    const storedUser = localStorage.getItem('username');
    if (storedUser) {
      setUsername(storedUser);
    } else {
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('username');
    navigate('/login');
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`予約内容\n往路: ${departureDate}\n復路: ${returnDate}`);
  };

  return (
    <div className="landing-container">
      <header className="landing-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <img src="/images/logo.png" alt="Logo" />
          <h1 className="landing-title">Tricentis 航空券予約</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
          <span style={{ fontWeight: 'bold', color: '#1e3a8a' }}>
            ようこそ {username} さん
          </span>
          <button onClick={handleLogout} className="landing-login-button">
            ログアウト
          </button>
        </div>
      </header>

      <main style={{ padding: '40px' }}>
        <h2>往復便の予約フォーム</h2>
        <form onSubmit={handleSubmit} style={{ maxWidth: '400px', marginTop: '20px' }}>
          <div style={{ marginBottom: '15px' }}>
            <label>往路の日付:</label><br />
            <input
              type="date"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
              required
              style={{ width: '100%', padding: '10px' }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label>復路の日付:</label><br />
            <input
              type="date"
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
              required
              style={{ width: '100%', padding: '10px' }}
            />
          </div>

          <button type="submit" style={{
            padding: '10px 20px',
            backgroundColor: '#4f46e5',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}>
            予約する
          </button>
        </form>
      </main>
    </div>
  );
}

export default HomePage;
