import React from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css'; // CSSファイルを忘れずに！

function LandingPage() {
  const navigate = useNavigate();

  const goToLogin = () => {
    navigate('/login');
  };

  return (
    <div className="landing-container">
      {/* ヘッダー */}
      <header className="landing-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <img
            src="/images/logo.png"
            alt="Logo"
          />
          <h1 className="landing-title">Tricentis Clone App</h1>
        </div>
        <button onClick={goToLogin} className="landing-login-button">
          ログイン
        </button>
      </header>

      {/* メイン */}
      <main className="landing-main">
        <div style={{ display: 'flex', alignItems: 'center', gap: '40px', flexWrap: 'wrap', justifyContent: 'center' }}>
          {/* テキスト */}
          <div style={{ maxWidth: '500px' }}>
            <h2 className="landing-heading">自動化テストの学習をもっと身近に</h2>
            <p className="landing-subtext">
              このアプリは、Selenium や Playwright などのテストツールのトレーニングに使える、実践的なサンプルアプリです。
              各種UI操作・認証・テーブル処理などの練習ができます。
            </p>
          </div>

          {/* 画像 */}
          <img
            src="/images/landing.jpg"
            alt="Hero"
            style={{
              borderRadius: '12px',
              boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
              maxWidth: '100%',
              height: 'auto',
            }}
          />
        </div>
      </main>
    </div>
  );
}

export default LandingPage;
