import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ConfirmationPage() {
  const location = useLocation();
  const navigate = useNavigate();

  const [price, setPrice] = useState(null);
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [age, setAge] = useState('');
  const [cardNumber, setCardNumber] = useState('');

  const {
    departureAirport,
    arrivalAirport,
    departureDate,
    returnDate,
    passengerCount,
  } = location.state || {};

  useEffect(() => {
    if (!departureAirport || !arrivalAirport) return;

    const fetchPrice = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/pricing', {
          params: {
            from: departureAirport,
            to: arrivalAirport,
          },
        });
        setPrice(res.data.price);
      } catch (err) {
        setPrice('取得エラー');
      }
    };

    fetchPrice();
  }, [departureAirport, arrivalAirport]);

  const handleConfirm = (e) => {
    e.preventDefault();

    if (!name || !address || !age || !cardNumber) {
      alert('すべての情報を入力してください。');
      return;
    }

    alert(`予約が確定しました！\n\n【登録者情報】\n名前: ${name}\n住所: ${address}\n年齢: ${age}\nカード番号: ${cardNumber}\n\nご利用ありがとうございました！`);
    navigate('/home');
  };

  if (!location.state) {
    return <p>予約データが見つかりません。</p>;
  }

  return (
    <div style={{ maxWidth: '600px', margin: '40px auto', padding: '20px', background: '#fff', borderRadius: '12px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
      <h2>予約内容の確認</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        <li><strong>出発地:</strong> {departureAirport}</li>
        <li><strong>到着地:</strong> {arrivalAirport}</li>
        <li><strong>往路:</strong> {departureDate}</li>
        <li><strong>復路:</strong> {returnDate}</li>
        <li><strong>人数:</strong> {passengerCount}人</li>
        <li><strong>合計金額:</strong> {price !== null ? `¥${price * passengerCount}` : '読み込み中...'}</li>
      </ul>

      <hr style={{ margin: '20px 0' }} />

      <form onSubmit={handleConfirm}>
        <div style={{ marginBottom: '10px' }}>
          <label>名前:</label><br />
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '100%', padding: '8px' }} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>住所:</label><br />
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} style={{ width: '100%', padding: '8px' }} />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label>年齢:</label><br />
          <input type="number" value={age} onChange={(e) => setAge(e.target.value)} style={{ width: '100%', padding: '8px' }} />
        </div>
        <div style={{ marginBottom: '20px' }}>
          <label>クレジットカード番号:</label><br />
          <input type="text" value={cardNumber} onChange={(e) => setCardNumber(e.target.value)} style={{ width: '100%', padding: '8px' }} />
        </div>

        <button type="submit" style={{
          width: '100%',
          padding: '12px',
          backgroundColor: '#4f46e5',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          fontWeight: 'bold',
          fontSize: '16px',
          cursor: 'pointer'
        }}>
          この内容で購入する
        </button>
      </form>
    </div>
  );
}

export default ConfirmationPage;
