import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LandingPage.css';

function SignupPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSignup = async () => {
    if (!username || !password) {
      setMessage('ユーザIDとパスワードを入力してください');
      return;
    }
    try {
      const res = await axios.post('http://localhost:5000/api/signup', { username, password });
      if (res.data.success) {
        alert('登録が完了しました！ログインしてください');
        navigate('/login');
      }
    } catch (err) {
      setMessage(err.response?.data?.message || '登録に失敗しました');
    }
  };

  return (
    <div className="landing-container" style={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundImage: 'url(/images/landing.jpg)', backgroundSize: 'cover', backgroundPosition: 'center' }}>
      <div style={{ backgroundColor: 'rgba(255,255,255,0.95)', padding: '40px', borderRadius: '12px', boxShadow: '0 8px 16px rgba(0,0,0,0.2)', width: '320px', textAlign: 'center' }}>
        <h2>新規登録</h2>
        <input
          type="text"
          placeholder="ユーザID"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ width: '100%', padding: '10px', margin: '10px 0' }}
        />
        <input
          type="password"
          placeholder="パスワード"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ width: '100%', padding: '10px', margin: '10px 0' }}
        />
        <button onClick={handleSignup} style={{ width: '100%', padding: '10px', backgroundColor: '#4f46e5', color: 'white', border: 'none', borderRadius: '6px', fontWeight: 'bold', cursor: 'pointer', marginTop: '10px' }}>
          登録
        </button>
        {message && <p style={{ color: 'red', marginTop: '10px' }}>{message}</p>}
      </div>
    </div>
  );
}

export default SignupPage;
