import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

function ConfirmationPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [price, setPrice] = useState(null);

  // location.state からデータを取得
  const {
    departureAirport,
    arrivalAirport,
    departureDate,
    returnDate,
    passengerCount,
  } = location.state || {};

  useEffect(() => {
    if (!departureAirport || !arrivalAirport) return;

    // Pricing API を呼び出して金額取得
    const fetchPrice = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/pricing', {
          params: {
            from: departureAirport,
            to: arrivalAirport,
          },
        });
        setPrice(res.data.price);
      } catch (err) {
        setPrice('取得エラー');
      }
    };

    fetchPrice();
  }, [departureAirport, arrivalAirport]);

  const handleConfirm = () => {
    alert('予約が確定しました！'); // 実際にはDB登録などに進めます
    navigate('/home'); // 予約後の遷移先
  };

  if (!location.state) {
    return <p>予約データが見つかりません。</p>;
  }

  return (
    <div style={{ maxWidth: '500px', margin: '40px auto', padding: '20px', background: '#fff', borderRadius: '12px', boxShadow: '0 4px 10px rgba(0,0,0,0.1)' }}>
      <h2>予約内容の確認</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        <li><strong>出発地:</strong> {departureAirport}</li>
        <li><strong>到着地:</strong> {arrivalAirport}</li>
        <li><strong>往路:</strong> {departureDate}</li>
        <li><strong>復路:</strong> {returnDate}</li>
        <li><strong>人数:</strong> {passengerCount}人</li>
        <li><strong>合計金額:</strong> {price !== null ? `¥${price * passengerCount}` : '読み込み中...'}</li>
      </ul>
      <button
        onClick={handleConfirm}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          backgroundColor: '#4f46e5',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          fontWeight: 'bold',
          cursor: 'pointer'
        }}
      >
        この内容で予約を確定
      </button>
    </div>
  );
}

export default ConfirmationPage;
