import React from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

function LandingPage() {
  const navigate = useNavigate();

  const goToLogin = () => {
    navigate('/login');
  };

  const goToReservation = () => {
    const user = localStorage.getItem('username');
    if (user) {
      navigate('/home');
    } else {
      navigate('/login');
    }
  };

  const goToLookup = () => {
    navigate('/lookup'); // ← 検索ページ（未作成ならあとで）
  };

  return (
    <div className="landing-container">
      {/* ヘッダー */}
      <header className="landing-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px', cursor: 'pointer' }} onClick={() => navigate('/')}>
          <img src="/images/logo.png" alt="Logo" />
          <h1 className="landing-title">Tricentis 航空券予約</h1>
        </div>
        <button onClick={goToLogin} className="landing-login-button">
          ログイン
        </button>
      </header>

      {/* メイン（背景画像含む） */}
      <main className="landing-main">
        <div style={{ display: 'flex', alignItems: 'center', gap: '40px', flexWrap: 'wrap', justifyContent: 'center' }}>
          <img
            src="/images/landing.jpg"
            alt="Hero"
            style={{
              borderRadius: '12px',
              boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
              maxWidth: '100%',
              height: 'auto',
            }}
          />
        </div>

        {/* メニュー追加 */}
        <div style={{
          marginTop: '40px',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: '20px',
          backgroundColor: 'rgba(255, 255, 255, 0.85)',
          padding: '30px',
          borderRadius: '12px',
          boxShadow: '0 4px 8px rgba(0,0,0,0.1)'
        }}>
          <button onClick={goToReservation} style={{
            padding: '16px 40px',
            fontSize: '18px',
            backgroundColor: '#4f46e5',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: 'bold',
            width: '250px'
          }}>
            航空券予約
          </button>
          <button onClick={goToLookup} style={{
            padding: '16px 40px',
            fontSize: '18px',
            backgroundColor: '#10b981',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontWeight: 'bold',
            width: '250px'
          }}>
            航空券確認
          </button>
        </div>
      </main>
    </div>
  );
}

export default LandingPage;
