import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

function HomePage() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');

  useEffect(() => {
    const storedUser = localStorage.getItem('username');
    if (storedUser) {
      setUsername(storedUser);
    } else {
      // 未ログインならリダイレクトなども可
      navigate('/login');
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('username'); // ログアウト時に削除
    navigate('/login');
  };

  return (
    <div className="landing-container">
      <header className="landing-header">
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <img src="/images/logo.png" alt="Logo" />
          <h1 className="landing-title">Tricentis 航空券予約</h1>
        </div>
        <div style={{ display: 'flex', alignItems: 'center', gap: '20px' }}>
          <span style={{ fontWeight: 'bold', color: '#1e3a8a' }}>
            ようこそ {username} さん
          </span>
          <button onClick={handleLogout} className="landing-login-button">
            ログアウト
          </button>
        </div>
      </header>

      <main style={{ padding: '40px' }}>
        <h2>ホーム画面</h2>
        <p>ようこそ、{username} さん。ここに予約フォームなどを追加していきます。</p>
      </main>
    </div>
  );
}

export default HomePage;
